import React from 'react';
import {Link} from 'react-router-dom';

const Book = (book) => {
  return (
    <div class="ui grid" className='book-item'>
    <div class="four wide column" className='book-item-img'>
        <img src = {book.cover_img} alt = "cover"/>
    </div>
    
  </div>
  )
}

export default Book
